changeSet.sql:
CREATE TABLE PUBLIC.FILE ( ID BIGSERIAL PRIMARY KEY, UUID UUID UNIQUE );
CREATE TABLE PUBLIC.ROLE ( ID BIGSERIAL PRIMARY KEY, NAME VARCHAR(255) NOT NULL UNIQUE );
CREATE TABLE PUBLIC.CITY ( ID BIGSERIAL PRIMARY KEY, PHOTO_FILE_ID BIGINT, NAME VARCHAR(255) NOT NULL, FOREIGN KEY (PHOTO_FILE_ID) REFERENCES FILE (ID) );
CREATE TABLE PUBLIC.USER ( ID BIGSERIAL PRIMARY KEY, ROLE_ID BIGINT NOT NULL, UUID UUID UNIQUE, USERNAME VARCHAR(50) NOT NULL, PASSWORD VARCHAR(255) NOT NULL, FIRST_NAME VARCHAR(255) NOT NULL, LAST_NAME VARCHAR(255) NOT NULL, FOREIGN KEY (ROLE_ID) REFERENCES ROLE (ID) );
CREATE TABLE PUBLIC.REFRESH_TOKEN ( ID BIGSERIAL PRIMARY KEY, USER_ID BIGINT, TOKEN UUID NOT NULL UNIQUE, EXPIRY_DATE TIMESTAMP WITHOUT TIME ZONE NOT NULL, FOREIGN KEY (USER_ID) REFERENCES PUBLIC.USER (ID) );

INSERT INTO PUBLIC.ROLE(NAME) VALUES ('ROLE_ADMIN');
INSERT INTO PUBLIC.ROLE(NAME) VALUES ('ROLE_ALLOW_EDIT');
INSERT INTO PUBLIC.ROLE(NAME) VALUES ('ROLE_READ_ONLY');
